package FlowControl;

public class SelectiveIFELSE {
	int i=9;
	 void m1() {
		if(i<=6)
		{
		System.out.println("hi");	
		}
		else {
			System.out.println("hello");
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		SelectiveIFELSE s=new SelectiveIFELSE ();
		s.m1();
	}

}
